// ... (previous code)

export default function Profile() {
  const [isOpen, setIsOpen] = useState(false);
  const [post, setPost] = useState(null);

  const handleCloseDialog = () => {
    setIsOpen(false);
  };

  const user = {
    profileImage: "https://example.com/profile.jpg",
    username: "YourUsername",
    fullName: "Your Full Name",
    followers: 100,
    following: 50,
    posts: posts.length,
    bio: "Your bio goes here.",
  };

  return (
    <>
      <Helmet>
        <title>Instagram</title>
      </Helmet>

      <Container>
        <TopBar>
          <div>Instagram Clone Coding</div>
          <div>
            <Button>프로필</Button>
            <Button>보관된 스토리 보기</Button>
            <Button>⚙️</Button>
          </div>
        </TopBar>

        <ProfileHeader>
          <img src={user.profileImage} alt="Profile" />
          <ProfileInfo>
            <h2>{user.username}</h2>
            <p>{user.fullName}</p>
            <p>{user.bio}</p>
          </ProfileInfo>
        </ProfileHeader>

        <Divider />

        <Grid>
          {posts.map((post) => (
            <Item key={post.postId}>
              <img
                src={post.image}
                alt={post.postText}
                onClick={() => {
                  setPost(post);
                  setIsOpen(true);
                }}
              />
              <p>{post.postText}</p>
            </Item>
          ))}
        </Grid>

        <Statistics>
          <StatisticItem>
            <strong>{user.posts}</strong>
            <p>게시글</p>
          </StatisticItem>
          <StatisticItem>
            <strong>{user.following}</strong>
            <p>팔로잉</p>
          </StatisticItem>
          <StatisticItem>
            <strong>{user.followers}</strong>
            <p>팔로워</p>
          </StatisticItem>
        </Statistics>
      </Container>

      {isOpen && (
        <PostDialog open={isOpen} onClose={handleCloseDialog} post={post} />
      )}
    </>
  );
}
