import { useState } from "react";
import { Helmet } from "react-helmet";
import styled from "styled-components";
import PostDialog from "./PostDialog";

const posts = [
  {
    postId: 1,
    postText: "리액트 공부중…",
    image:
      "https://velog.velcdn.com/images/dooooh2/post/e03d49ee-8c38-4195-ae3e-1ca6668d9581/image.png",
  },
  {
    postId: 2,
    postText: "마라탕먹고싶다",
    image: "https://www.foodjang.com/New/05/221806880/221806880_b_1.jpg",
  },
  {
    postId: 3,
    postText: "오늘은 뭐하지",
    image:
      "https://www.thecookierookie.com/wp-content/uploads/2018/07/bulletproof-coffee-recipe-5-of-9.jpg",
  },
];

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column; /* 변경된 부분 */
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px; /* 추가된 부분 */
  width: 1000px;
  max-width: 100%; /* 변경된 부분 */
  margin-top: 20px; /* 변경된 부분 */
`;

const Item = styled.div`
  width: 100%;
  height: 100%;
  max-width: 300px; /* 변경된 부분 */
  margin: 10px;
  padding: 10px;
  border: 1px solid #ddd; /* 추가된 부분 */
  border-radius: 8px; /* 추가된 부분 */
  overflow: hidden; /* 추가된 부분 */
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); /* 추가된 부분 */

  img {
    width: 100%;
    height: 200px; /* 변경된 부분 */
    object-fit: cover;
    border-radius: 8px; /* 추가된 부분 */
    margin-bottom: 10px; /* 추가된 부분 */
  }

  p {
    margin: 0;
    font-size: 14px;
    color: #333;
  }
`;

export default function Profile() {
  const [isOpen, setIsOpen] = useState(false);
  const [post, setPost] = useState(null);

  const handleCloseDialog = () => {
    setIsOpen(false);
  };

  return (
    <>
      <Helmet>
        <title>Instagram</title>
      </Helmet>

      <Container>
        <h1>Instagram Clone</h1>
        <Grid>
          {posts.map((post) => (
            <Item key={post.postId}>
              <img
                src={post.image}
                alt={post.postText}
                onClick={() => {
                  setPost(post);
                  setIsOpen(true);
                }}
              />
              <p>{post.postText}</p>
            </Item>
          ))}
        </Grid>
      </Container>

      {isOpen && (
        <PostDialog open={isOpen} onClose={handleCloseDialog} post={post} />
      )}
    </>
  );
}
