import { useState } from "react";
import { Helmet } from "react-helmet";
import styled from "styled-components";
import PostDialog from "./PostDialog";

const posts = [
  // ... (이전 코드와 동일)
];

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ProfileHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;

  img {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    margin-right: 20px;
  }
`;

const UserInfo = styled.div`
  display: flex;
  flex-direction: column;

  h2 {
    margin-bottom: 5px;
  }

  p {
    color: gray;
    margin-bottom: 5px;
  }
`;

const Stats = styled.div`
  display: flex;
  margin-top: 10px;

  div {
    margin-right: 20px;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  width: 1000px;
`;

const Item = styled.div`
  // (이전 코드와 동일)
`;

export default function Profile() {
  const [isOpen, setIsOpen] = useState(false);
  const [post, setPost] = useState(null);

  const handleCloseDialog = () => {
    setIsOpen(false);
  };

  // 가상의 사용자 정보
  const user = {
    profileImage: "https://example.com/profile.jpg",
    username: "YourUsername",
    fullName: "Your Full Name",
    followers: 100,
    following: 50,
    posts: posts.length, // 현재 코드에서는 포스트 배열의 길이를 사용
    bio: "Your bio goes here.",
  };

  return (
    <>
      <Helmet>
        <title>Instagram</title>
      </Helmet>

      <Container>
        <ProfileHeader>
          <img src={user.profileImage} alt="Profile" />
          <UserInfo>
            <h2>{user.username}</h2>
            <p>{user.fullName}</p>
          </UserInfo>
        </ProfileHeader>

        <Stats>
          <div>
            <strong>{user.posts}</strong> 게시글
          </div>
          <div>
            <strong>{user.followers}</strong> 팔로워
          </div>
          <div>
            <strong>{user.following}</strong> 팔로잉
          </div>
        </Stats>

        <p>{user.bio}</p>

        <Grid>
          {posts.map((post) => (
            <Item key={post.postId}>
              <img
                src={post.image}
                alt={post.postText}
                onClick={() => {
                  setPost(post);
                  setIsOpen(true);
                }}
              />
              <p>{post.postText}</p>
            </Item>
          ))}
        </Grid>
      </Container>

      {isOpen && (
        <PostDialog open={isOpen} onClose={handleCloseDialog} post={post} />
      )}
    </>
  );
}
